  $scope.navigate = function (e) {
    //console.log(e.keyCode);
    //console.log($scope.filteredTags);
    var direction = null,
        index = $scope.selected,
        length = $scope.filteredTags.length - 1;

    if (e.keyCode == 27) {
      // escape
      $scope.search.tag = '';
      $scope.selected = 0;
    }

    if (e.keyCode == 8) {
      // delete
      if (!$scope.search || $scope.search.tag.length === 0) {
        $scope.createdTags.pop();
      }
    }

    if (e.keyCode == 13) {
      // enter
      e.stopPropagation();
      e.preventDefault(); //console.log($scope.filteredTags);

      if ($scope.filteredTags.length !== 0) {
        $scope.selectTag();
      } else {
        $scope.createTag();
      }
    }

    if (e.keyCode == 38) {
      direction = -1;
    } // up


    if (e.keyCode == 40) {
      direction = 1;
    } // down


    if (direction) {
      e.stopPropagation();
      index += direction;
      if (index < 0) index = length;
      if (index > length) index = 0;
      $scope.selected = index;
    }
  };

